import React from 'react'

function Home(){
    const currentTime = new Date()
    const currentHour = currentTime.getHours() 
    

    let greet
    if(currentHour < 12){
        greet = 'Good Morning !'
    } else if (currentHour < 17 ){
        greet = 'Good Afternoon !'
    }else{
        greet = 'Good Evening !'
    }
    return(
        <div>
            <h3 style = {{color:'rgb(114, 6, 51)',fontStyle:'inherit',textAlign:'start',lineHeight: 3}}>.... {greet} </h3>

            Welcome !!!
            
            A Simple Contacts App with OTP SMS Sending Functionality
        </div>
    )
}

export default Home
