import React from 'react'
import axios from '../../config/axios'
import { Link } from 'react-router-dom'

class Message extends React.Component{
    constructor(){
        super()
        this.state = {
            messages : []
        }
    }

    componentDidMount(){
        axios.get('/messages')
            .then(response => {
                const messages = response.data
                this.setState({messages})
            })
            .catch(err => {
                console.log(err)
            })
    }


    render(){
        return(
            <div>
                <h4>OTP Send List:-</h4>
                <ul>
                {
                    this.state.messages.map(message => {
                        return <li key={message._id}>{message.name}-{message.createdAt}<Link to={`/messages/${message._id}`}>Info</Link></li>
                    })
                }
                </ul>
            </div>
        )
    }
}

export default Message
