import React from 'react'
import axios from '../../config/axios'
import { Link } from 'react-router-dom'

class List extends React.Component{
    constructor(){
        super()
        this.state = {
            contacts : []
        }
    }

    componentDidMount(){
        axios.get('/contacts')
            .then(response => {
                const contacts = response.data
                this.setState({contacts})
                console.log('contact',contacts)
            })
            .catch(err => {
                console.log(err)
            })
    }

    handleRemove = (id) => {
        axios.delete(`/customers/${id}`)
        .then(response => {
            this.setState(prevState => ({
                contacts: prevState.contacts.filter(contact => contact._id != response.data._id)
            }))
        })

    }

    render(){
        return(
            <div>
                <h3>Contact List:-{this.state.contacts.length}</h3>
                    <ul>
                        {
                            this.state.contacts.map(contact => {
                                return <li key={contact._id}>{contact.firstName} { contact.lastName} <Link to={`/contacts/${contact._id}`}>Info</Link> 
                                <button onClick={() => {
                                            this.handleRemove(contact._id)
                                            }} className="btn btn-danger">Remove</button></li>
                            })
                        }
                    </ul>
                <Link to="/contacts/add">Add</Link>
            </div>
        )
    }
}

export default List
