import React from 'react'
import axios from '../../config/axios'
import { Link } from 'react-router-dom'

class Message extends React.Component{
    constructor(){
        super()
        this.state = {
            messages : []
        }
    }

    componentDidMount(){
        axios.get('/messages')
            .then(response => {
                const messages = response.data
                this.setState({messages})
            })
            .catch(err => {
                console.log(err)
            })
    }

        handleRemove = (id) => {
            axios.delete(`/messages/${id}`)
            .then(response => {
                this.setState(prevState => ({
                    messages: prevState.messages.filter(message => message._id != response.data._id)
                }))
            })

        }


    render(){
        return(
            <div style={{margin:'60px 0px 0px 0px'}}>
                <h4>OTP Sent List:- {this.state.messages.length}</h4>
                <ul>
                {
                    this.state.messages.map(message => {
                        return <div  class="list-group">
                            <a href={`/messages/${message._id}`} class="list-group-item list-group-item-action" key={message._id}>{message.name}-{message.createdAt}
                             <button onClick={() => {this.handleRemove(message._id)
                                            }} className="btn btn-danger">Remove</button>
                                            </a></div>
                    })
                }
                </ul>
            </div>
        )
    }
}

export default Message

