import React from 'react'
// import SimpleReactValidator from 'simple-react-validator'
import axios from '../../config/axios'

class Add extends React.Component{
    constructor(){
        super()
        // this.validator = new SimpleReactValidator()
        this.state = {
            firstName : '',
            lastName : '',
            mobile : ''
        }
    }

    handleSubmit = (e) => {
        e.preventDefault()
        const formData = {
            firstName : this.state.firstName,
            lastName : this.state.lastName,
            mobile : this.state.mobile
        }
        this.setState({formData})
        console.log('formData',formData)
    
        axios.post('/contacts' , formData )
            .then(response => {
                const form = response.data
                this.setState({form})
                console.log('form',form)
                this.props.history.push('/contacts')
            })
            .catch(err => {
                console.log(err)
            })
    }

    

    handleChange = (e) => {
        const form = e.target.value
        this.setState({
            [e.target.name] : e.target.value
        })
        console.log('form', form)
    }


    render(){
        return(
            <div style={{margin:'50px 0px 0px 0px'}}>
                <h4><p class="font-weight-bolder">Add new Contact: </p></h4>

                <form onSubmit={this.handleSubmit} >

                <div class="form-group" class="col-md-2" class="form-inline">
                <label htmlFor="firstName">  First Name:</label>
                <input type="text" id="firstName"  name="firstName" onChange={this.handleChange} class="form-control mx-sm-3"  /> </div><br/>
                
                <div class="form-group"  class="col-md-2" class="form-inline" >
                <label htmlFor="lastName">Last Name:</label>
                <input type="text" id="lastName" name="lastName" onChange={this.handleChange} class="form-control mx-sm-3" /> </div><br/>
                
                <div class="form-group" class="col-md-2" class="form-inline">
                <label htmlFor="mobile">Mobile:</label>
                <input type="text" id="mobile"  name="mobile" onChange={this.handleChange} class="form-control mx-sm-3" /> 
                {/* {this.validator.message('mobile', this.state.mobile, 'required|min:10|max:10')} */}
                <small id="mobile" class="text-muted">
                <br />Mobile No. Must Be 10 Numbers.
                </small> </div><br/>

                <input type="submit" value="Add" class="btn btn-primary"  />

                </form>
            </div>
        )
    }
}

export default Add
