import React from 'react'
import axios from '../../config/axios'
import { Link } from 'react-router-dom'

class Info extends React.Component{
    constructor(){
        super()
        this.state = {
            contacts : {}
        }
    }

    componentDidMount(){
        const id = this.props.match.params.id
        axios.get(`/contacts/${id}`)
        .then(response => {
            const contacts = response.data
            this.setState({contacts})
            console.log('Contacts',contacts)
        })
        .catch(err => {
            console.log(err)
        })
    }

    render(){
        return(
            <div>
                <h3>Contact Info :</h3>
                <dl class="row">
                <dt class="col-sm-2"> Name </dt>
                <dd class="col-sm-10">{this.state.contacts.firstName} {this.state.contacts.lastName}</dd><br/>
                <li class="col-sm-9"><h5> Mobile No. :- {this.state.contacts.mobile}</h5></li>
                </dl>
                       
                <Link to={`/contacts/${this.state.contacts._id}/send`}>Send Message |</Link>

                <Link to="/contacts">Back</Link>
            </div>
        )    
    }
}

export default Info
