import React from 'react'
import axios from '../../config/axios'
import { Link } from 'react-router-dom'

class List extends React.Component{
    constructor(){
        super()
        this.state = {
            contacts : []
        }
    }

    componentDidMount(){
        axios.get('/contacts')
            .then(response => {
                const contacts = response.data
                this.setState({contacts})
                console.log('contact',contacts)
            })
            .catch(err => {
                console.log(err)
            })
    }

    handleRemove = (id) => {
        axios.delete(`/contacts/${id}`)
        .then(response => {
            this.setState(prevState => ({
                contacts: prevState.contacts.filter(contact => contact._id != response.data._id)
            }))
        })

    }

    render(){
        return(
            <div>
                <p class="font-weight-bolder">Contact List:- {this.state.contacts.length}</p>
                    <table className="table" >
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Action</th>
                            <th>Remove</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.contacts.map((contact , i) => {
                                return (
                                    <tr key={contact._id}>
                                        <td>{i + 1}</td>
                                        <td>{contact.firstName}</td>
                                        <td>{contact.lastName}</td>
                                        <td><Link to={`/contacts/${contact._id}`}>Info</Link></td>
                                        <td><button onClick={() => {
                                            this.handleRemove(contact._id)
                                            }} className="btn btn-danger">Remove</button></td>
                                </tr>
                                ) 
                            })
                        }
                    </tbody>
                    </table>
                <Link to="/contacts/add">Add</Link>
            </div>
        )
    }
}

export default List
