import React from 'react'
import axios from '../../config/axios'

class Add extends React.Component{
    constructor(){
        super()
        this.state = {
            firstName : '',
            lastName : '',
            mobile : ''
        }
    }

    handleSubmit = (e) => {
        e.preventDefault()
        const formData = {
            firstName : this.state.firstName,
            lastName : this.state.lastName,
            mobile : this.state.mobile
        }
        this.setState({formData})
        console.log('formData',formData)
    
        axios.post('/contacts' , formData )
            .then(response => {
                const form = response.data
                this.setState({form})
                console.log('form',form)
            })
            .catch(err => {
                console.log(err)
            })
    }

    

    handleChange = (e) => {
        const form = e.target.value
        this.setState({
            [e.target.name] : e.target.value
        })
        console.log('form', form)
    }


    render(){
        return(
            <div>
                <h2>Add new Contact: </h2>

                <form onSubmit={this.handleSubmit}>

                <label htmlFor="firstName">First Name:</label>
                <input type="text" id="firstName" name="firstName" onChange={this.handleChange} /> <br/>
                
                <label htmlFor="lastName">Last Name:</label>
                <input type="text" id="lastName" name="lastName" onChange={this.handleChange} /> <br/>
                
                <label htmlFor="mobile">Mobile:</label>
                <input type="text" id="mobile" name="mobile" onChange={this.handleChange} /> <br/>

                <input type="submit" value="Add" />

                </form>
            </div>
        )
    }
}

export default Add
