import React from 'react'
import axios from '../../config/axios'
import { Link } from 'react-router-dom'

class Info extends React.Component{
    constructor(){
        super()
        this.state = {
            contacts : {}
        }
    }

    componentDidMount(){
        const id = this.props.match.params.id
        axios.get(`/contacts/${id}`)
        .then(response => {
            const contacts = response.data
            this.setState({contacts})
            console.log('Contacts',contacts)
        })
        .catch(err => {
            console.log(err)
        })
    }

    render(){
        return(
            <div>
                <h3>Contact Info :</h3>
                <ul class="list-group list-group-flush">
                <h4>Name :-   <li class="list-group-item">{this.state.contacts.firstName} {this.state.contacts.lastName}</li></h4>
                <h5> Mobile No. :-   <li class="list-group-item">{this.state.contacts.mobile}</li>   </h5>
                </ul>
                       
                <Link to={`/contacts/${this.state.contacts._id}/send`}>Send Message |</Link>

                <Link to="/contacts">Back</Link>
            </div>
        )    
    }
}

export default Info
