import React from 'react'

class Show extends React.Component{
    constructor(){
        super()
        this.state = {
            messages : {}
        }
    }

    componentDidMount(){
        const id = this.props.match.params.id
        axios.get(`/messages/${id}`)
        .then(response => {
            const messages = response.data
            this.setState({messages})
            console.log('messages',messages)
        })
        .catch(err => {
            console.log(err)
        })
    }

    render(){
        return(
            <div>

            </div>
        )
    }
}

export default Show
